import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom'

const Table = () => {

    const navigate = useNavigate();


    const [allData, setAllData] = useState([])

    useEffect(() => {
        const fetchData = async () => {
            fetch("http://localhost:5000/api/")
                .then((response) => response.json())
                .then((data) =>
                    setAllData(data.cruds)
                ).catch((err) => {
                    // // console.log('err::: ', err);
                })
        }
        fetchData();
    }, [])

    const deleteData = async (id) => {
        // console.log('id::: ', id);
        fetch(`http://localhost:5000/api/delete/${id}`,
            {
                method: "DELETE",
                headers: {
                    "Content-Type": "application/json",
                }
            })
            .then((response) => response.json())
            .then((data) => {
                // console.log('data::: ', data.message)
                if (data.message === "The student was removed") {
                    const delete_data = allData.filter((ele, index) => ele._id !== id);
                    setAllData(delete_data)
                }
            }
            ).catch((err) => {
                // console.log('err::: ', err)
            })
    }

    const editData = (id) => {
        navigate(`/edit?${id}`)
        console.log('id::: ', id);
    }

    return (
        <>

            <center>
                <table>
                    <thead>
                        <tr>
                            <td>Blog title</td>
                            <td>Blog Description</td>

                        </tr>
                    </thead>
                    <tbody>
                        {
                            allData.map((data, index) => {
                                return (
                                    <tr key={ index }>
                                        <td>{ index + 1 }</td>
                                        <td>{ data.name }</td>

                                        <td>
                                            <div>
                                                <button onClick={ () => deleteData(data._id) }>Delete</button>
                                                <button onClick={ () => editData(data._id) }>Edit</button>
                                            </div>
                                        </td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                </table>
            </center >
        </>
    )
}

export default Table
